import React, { useContext, useState } from 'react';
import { Platform, StyleSheet, View } from 'react-native';
import { DrawerContentScrollView } from '@react-navigation/drawer';
import { useNavigation } from '@react-navigation/native';
import {
  Drawer,
  Switch,
  Text,
  TouchableRipple,
} from 'react-native-paper';

import { PreferencesContext, useExampleTheme } from './index';

const isWeb = Platform.OS === 'web';

const DrawerItemsData = [
  {
    label: 'Login',
    icon: 'login',
    key: -1,
  },
  {
    label: 'Notícies',
    icon: 'newspaper',
    key: 0,
  },
  {
    label: 'Tramits',
    icon: 'file-edit',
    key: 1,
  },
  { 
    label: 'Informació', 
    icon: 'send', 
    key: 2 
  },
];

function DrawerItems() {

  const navigation = useNavigation();

  const [drawerItemIndex, setDrawerItemIndex] = useState<number>(0);
  const preferences = useContext(PreferencesContext);

  const _setDrawerItem = (index: number) => setDrawerItemIndex(index);

  const { isV3, colors } = useExampleTheme();

  if (!preferences) throw new Error('PreferencesContext not provided');

  const {
    toggleTheme,
    collapsed,
    theme: { dark: isDarkTheme },
  } = preferences;

  const coloredLabelTheme = {
    colors: isV3
      ? {
          secondaryContainer: '#CAC4D0',
          onSecondaryContainer: '#CAC4D0',
        }
      : {
          primary: '#009688',
        },
  };

  return (
    <DrawerContentScrollView
      alwaysBounceVertical={false}
      style={[
        styles.drawerContent,
        {
          backgroundColor: colors.surface,
        },
      ]}
    >
      <Drawer.Section style={{ paddingTop: 10 }}>
          <Drawer.Item
            label="Login"
            icon="login"
            active={drawerItemIndex === -1} 
            onPress={() => navigation.navigate('Login')}
            
            style={{ backgroundColor: '#2F29A1' }}
            theme={{colors: { 
            onSurface: '#CAC4D0', }}}
          />
      </Drawer.Section>

      {!collapsed && (
        <>
          <Drawer.Section title="Recursos Generals">
            {DrawerItemsData.filter(item => item.key !== -1).map((props, index) => (
              <Drawer.Item
                {...props}
                key={props.key}
                theme={props.key === 3 ? coloredLabelTheme : undefined}
                active={drawerItemIndex === index}
                onPress={() => _setDrawerItem(index)}
              />
            ))}
          </Drawer.Section>
          <Drawer.Section>
              <TouchableRipple onPress={toggleTheme}>
                <View style={[styles.preference, isV3 && styles.v3Preference]}>
                  <Text variant="labelLarge">Dark Theme</Text>
                  <View pointerEvents="none">
                    <Switch value={isDarkTheme} />
                  </View>
                </View>
              </TouchableRipple>
            </Drawer.Section>
          </>
      )}
    </DrawerContentScrollView>
  );
}

const styles = StyleSheet.create({
  drawerContent: {
    flex: 1,
  },
  preference: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 12,
    paddingHorizontal: 16,
  },
  v3Preference: {
    height: 56,
    paddingHorizontal: 28,
  },
});

export default DrawerItems;
