import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { jwtDecode } from 'jwt-decode';

function decodeToken(token) {
    try {
        const decoded = jwtDecode(token);
        return decoded;
    } catch (error) {
        console.error("Error decoding token: ", error);
        return null;
    }
}

type UserRole = 0 | 1 | 2 | 3 | 4; // 0: Logout, 1: User 2: Alumno, 3: Profesor, 4: Admin

interface UserContextType {
    userRole: UserRole;
    setUserRole: (role: UserRole) => void;
}

const UserContext = createContext<UserContextType | undefined>(undefined);

export const useUser = (): UserContextType => {
    const context = useContext(UserContext);
    if (!context) {
        throw new Error('useUser must be used within a UserProvider');
    }
    return context;
};

interface UserProviderProps {
    children: ReactNode;
}

export const UserProvider: React.FC<UserProviderProps> = ({ children }) => {
    const [userRole, setUserRole] = useState<UserRole>(0);

    const loadUserRoleFromStorage = async () => {
        const token = await AsyncStorage.getItem('userToken');
        if (token) {
            // Aquí debes añadir la lógica para decodificar el token y determinar el rol del usuario
            // Esto es solo un ejemplo, ajusta según tu esquema de autenticación
            const decodedToken = decodeToken(token); // Esta función debe ser implementada para decodificar tu token JWT o similar
            setUserRole(decodedToken.role);
        }
    };

    useEffect(() => {
        loadUserRoleFromStorage();
    }, []);

    return (
        <UserContext.Provider value={{ userRole, setUserRole }}>
            {children}
        </UserContext.Provider>
    );
};
