import axios from 'axios';
import CookieManager from 'react-native-cookies';
import AsyncStorage from '@react-native-async-storage/async-storage';

const API_URL = 'http://localhost:8082/api';

export const loginUser = async (email: string, password: string): Promise<void> => {
    try {
        // Hacer el POST para iniciar sesión
        const response = await axios.post(`${API_URL}/auth/signin`, {
        email,
        password,
        });

        // Tras iniciar sesión, las cookies son manejadas automáticamente por el navegador,
        // pero en React Native necesitas gestionarlas manualmente.
        console.log('Login successful:', response.data);

        // Aquí puedes acceder a las cookies si necesitas procesar algo específico
        const cookies = await CookieManager.get(API_URL);
        console.log('Cookies:', cookies);

        // Suponiendo que la cookie del JWT se llama 'jwtToken'
        const jwtToken = cookies.jwtToken.value;
        console.log('JWT Token:', jwtToken);

        // Guardar el JWT en AsyncStorage
        await AsyncStorage.setItem('jwtToken', jwtToken);
        console.log('JWT Token saved to AsyncStorage');

    } catch (error) {
        console.error('Login Error:', error);
    }
}

export const getJwtToken = async (): Promise<string | null> => {
    try {
        const jwtToken = await AsyncStorage.getItem('jwtToken');
        return jwtToken;
    } catch (error) {
        console.error('Error fetching JWT from AsyncStorage:', error);
        return null;
    }
}
