import * as React from 'react';
import { ScrollView, View, Text } from 'react-native';
import { useFocusEffect } from '@react-navigation/native'; // Usar useFocusEffect si necesitas recargar los datos cada vez que se visite la pantalla
import { getAllUsers } from '../services/adminService';

const UsersSection = () => {
    const [users, setUsers] = React.useState([]);
    const [loading, setLoading] = React.useState(true); // Estado para manejar la carga de datos

    const fetchUsers = async () => {
        setLoading(true);
        try {
            const data = await getAllUsers(); // Obtener los datos de los usuarios
            setUsers(data); // Actualizar el estado con los nuevos usuarios
        } catch (error) {
            console.error('Failed to fetch users:', error); // Gestionar errores si la peticiÃ³n falla
        } finally {
            setLoading(false); // Finalizar la carga independientemente del resultado
        }
    };

    useFocusEffect(
        React.useCallback(() => {
            fetchUsers(); // Llamar a fetchUsers cuando el componente gane foco
        }, [])
    );

    if (loading) {
        return (
            <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                <Text>Cargando usuarios...</Text>
            </View>
        );
    }

    return (
        <ScrollView style={{ padding: 20 }}>
            {users.map((user, index) => (
                <View key={index} style={{ marginBottom: 20 }}>
                    <Text>Username: {user.username}</Text>
                    <Text>Email: {user.email}</Text>
                    <Text>Role: {user.role.name}</Text>
                </View>
            ))}
        </ScrollView>
    );
};

export default UsersSection;
