import * as React from 'react';
import { View, StyleSheet } from 'react-native';
import { TouchableRipple, Text, useTheme } from 'react-native-paper';
import { MaterialCommunityIcons } from '@expo/vector-icons';

// DefiniciÃ³n de la interfaz para los props del componente
interface CustomDrawerItemProps {
  label: string;
  icon: any
  active: boolean;
  onPress: () => void; // Define el tipo para el prop onPress
}

const CustomDrawerItem: React.FC<CustomDrawerItemProps> = ({ label, icon, active, onPress }) => {
  const { colors } = useTheme();
  const backgroundColor = '#2F29A1';
  const textColor = '#CAC4D0';
  const iconColor = '#CAC4D0';

  return (
    <TouchableRipple onPress={onPress} style={[styles.item, { backgroundColor }]}>
      <View style={styles.content}>
        <MaterialCommunityIcons name={icon} size={24} color={iconColor} />
        <Text style={[styles.text, { color: textColor }]}>{label}</Text>
      </View>
    </TouchableRipple>
  );
};

const styles = StyleSheet.create({
  item: {
    paddingVertical: 8,
    paddingHorizontal: 16,
    flexDirection: 'row',
    alignItems: 'center',
  },
  content: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  text: {
    marginLeft: 32,
  },
});

export default CustomDrawerItem;
