import { Background } from '@react-navigation/elements';
import * as React from 'react';
import { ScrollView } from 'react-native';
import { List, useTheme } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../RootStackParamList';
import { PreferencesContext } from '../index';

import itemsData from '../data/itemsData.json';

const QualificacionsSection = () => {
    
    const { theme } = React.useContext(PreferencesContext);
    
    type NavigationProp = StackNavigationProp<RootStackParamList, 'QualificacionsSection'>

    const navigation = useNavigation<NavigationProp>();

    return (
        <ScrollView>
            {itemsData.map((item) => (
                <List.Accordion
                    key={item.id}
                    title={item.title}
                    left={(props) => <List.Icon {...props} icon={item.icon} />}
                    style={{
                        backgroundColor: theme.colors.listItem,
                        margin: 15,
                        marginTop: 15,
                        borderRadius: 10,
                        shadowColor: "#000",
                        shadowOffset: {
                            width: 0,
                            height: 2,
                        },
                        shadowOpacity: 0.23,
                        shadowRadius: 2.62,
                        elevation: 4, // Estilo de sombreado para Android
                    }}
                >
                    {/* Aquí puedes colocar cualquier contenido que desees mostrar dentro del acordeón */}
                    {/* Por ejemplo, un item de lista con el subtítulo y el texto */}
                    <List.Item
                        title={`Subtítulo: ${item.subtitle}`}
                        description={`Texto: ${item.text}`}
                        titleNumberOfLines={5}
                        descriptionNumberOfLines={5}
                    />
                </List.Accordion>
            ))}
        </ScrollView>
    );
}

export default QualificacionsSection;
