import { Background } from '@react-navigation/elements';
import * as React from 'react';
import { ScrollView, View, Text } from 'react-native';
import { List, useTheme } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../RootStackParamList';
import { PreferencesContext } from '../index';

import qualificacionsData from '../data/qualificacionsData.json';

const QualificacionsSection = () => {
    
    const { theme } = React.useContext(PreferencesContext);
    const [expandedIds, setExpandedIds] = React.useState(new Set());

    const handlePress = (id) => {
        const newSet = new Set(expandedIds);
        if (newSet.has(id)) {
            newSet.delete(id);
        } else {
            newSet.add(id);
        }
        setExpandedIds(newSet);
    };

    return (
            <ScrollView>
                {qualificacionsData.map((item) => (
                    <View
                        key={item.id}
                        style={{
                            backgroundColor: theme.colors.listItem,
                            margin: 15,
                            marginTop: 15,
                            marginBottom: 0,
                            borderRadius: 10,
                            shadowColor: "#000",
                            shadowOffset: { width: 0, height: 2 },
                            shadowOpacity: 0.23,
                            shadowRadius: 2.62,
                            elevation: 4,
                        }}
                    >
                        <List.Accordion
                            title={item.title}
                            expanded={expandedIds.has(item.id)}
                            onPress={() => handlePress(item.id)}
                            right={() => (
                                <View style={{
                                    backgroundColor: theme.colors.quadratNotes,
                                    width: 50, 
                                    height: 50, 
                                    justifyContent: 'center', 
                                    alignItems: 'center',
                                    borderRadius: 10
                                }}>
                                    <Text style={{
                                        color: theme.colors.onQuadratNotes,
                                        fontSize: 20,
                                        fontWeight: '500'
                                    }}>
                                        {item.nota}
                                    </Text>
                                </View>
                            )}
                            style={{
                                backgroundColor: theme.colors.listItem,
                                borderRadius: 10,
                                borderBottomEndRadius: expandedIds.has(item.id) ? 0 : 10,
                                borderBottomStartRadius: expandedIds.has(item.id) ? 0 : 10,
                            }}
                        >
                            <Text style={{padding: 10}}>
                                {item.professor} {/* Muestra la descripción del profesor solo cuando el acordeón está abierto */}
                            </Text>
                            {item.ufs.map((uf, index) => (
                                <List.Item
                                    key={index}
                                    title={uf.name}
                                    description={uf.detail}
                                    left={props => <List.Icon {...props} icon="book-outline" />}
                                    right={() => (
                                        <View style={{
                                            backgroundColor: theme.colors.quadratNotes,
                                            width: 50, 
                                            height: 50, 
                                            justifyContent: 'center', 
                                            alignItems: 'center',
                                            borderRadius: 10
                                        }}>
                                            <Text style={{
                                                color: theme.colors.onQuadratNotes,
                                                fontSize: 20,
                                                fontWeight: 'bold'
                                            }}>
                                                {uf.nota}
                                            </Text>
                                        </View>
                                    )}
                                    style={{}}
                                />
                            ))}
                        </List.Accordion>
                    </View>
                ))}
            </ScrollView>
        );
    }
    

export default QualificacionsSection;
