import React from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';

// Datos de ejemplo para la sección de noticias
const newsData = [
  {
    id: '1',
    title: 'Noticia 1',
    summary: 'Resumen de la noticia 1',
  },
  {
    id: '2',
    title: 'Noticia 2',
    summary: 'Resumen de la noticia 2',
  },
  // Agrega más noticias como desees...
];

// Componente para renderizar cada artículo de noticia
const NewsItem = ({ title, summary, onPress }) => (
  <TouchableOpacity onPress={onPress} style={styles.itemContainer}>
    <Text style={styles.title}>{title}</Text>
    <Text>{summary}</Text>
  </TouchableOpacity>
);

// Componente de la sección de noticias
const NewsSection = () => {
  const renderItem = ({ item }) => (
    <NewsItem
      title={item.title}
      summary={item.summary}
      onPress={() => console.log(`Noticia ${item.id} presionada`)}
    />
  );

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Noticias</Text>
      <FlatList
        data={newsData}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />
    </View>
  );
};

// Estilos del componente
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    paddingVertical: 10,
    paddingHorizontal: 15,
  },
  itemContainer: {
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#cccccc',
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default NewsSection;
