import React, { useState } from 'react';
import { View, Alert, Image, TouchableOpacity } from 'react-native';
import { TextInput, Button } from 'react-native-paper';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { GoogleLogin } from '@react-oauth/google'; // Importa GoogleLogin
import { useExampleTheme } from './index'; // Utiliza el tema, asumiendo que está exportado desde './index'

const Login = () => {
    const theme = useExampleTheme();
    const height = theme.isV3 ? 80 : 56;

    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = () => {
        console.log('Username:', userName);
        console.log('Password:', password);
    };

    const responseMessage = (response) => {
        console.log(response);
    };

    const handleGoogleLogin = (googleUser) => {
        const profile = googleUser.getBasicProfile();
        console.log('Google Profile:', profile);
        // Aquí puedes manejar la lógica de inicio de sesión con Google
    };

    return (
        <View style={{ flex: 1, justifyContent: 'center' }}>
            <View style={{ width: '80%', alignSelf: 'center' }}>
                <TextInput
                    label="Email"
                    value={userName}
                    onChangeText={setUserName}
                />
                <TextInput
                    label="Password"
                    value={password}
                    secureTextEntry
                    onChangeText={setPassword}
                />
                <Button mode="contained" onPress={handleLogin} style={{ marginTop: 20, height }}>
                    Login
                </Button>
                <GoogleOAuthProvider clientId="<your_client_id>">...</GoogleOAuthProvider>;
            </View>
        </View>
    );
};

export default Login;
