import React, { useState } from 'react';
import { View, Alert, Image, TouchableOpacity } from 'react-native';
import { TextInput, Button } from 'react-native-paper';

import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google'; // Importa GoogleLogin
import { useExampleTheme } from './index'; // Utiliza el tema, asumiendo que estÃ¡ exportado desde './index'

const Login = () => {
    const theme = useExampleTheme();
    const height = theme.isV3 ? 80 : 56;

    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = () => {
        console.log('Username:', userName);
        console.log('Password:', password);
    };

    return (
        <View style={{ flex: 1, justifyContent: 'center' }}>
            <View style={{ width: '80%', alignSelf: 'center' }}>
                <TextInput style={{ marginTop: 20, marginBottom: 20, height }}
                    label="Email"
                    value={userName}
                    onChangeText={setUserName}
                    mode="outlined"
                />
                <TextInput  style={{ marginTop: 20, marginBottom: 20, height }}
                    label="Password"
                    value={password}
                    secureTextEntry
                    onChangeText={setPassword}
                    mode="outlined"
                />
                <Button mode="contained" onPress={handleLogin} 
                style={{marginTop: 20, marginBottom: 20, height, justifyContent: 'center' }}
                textColor="#FFFFFF">
                    Login
                </Button>
                <GoogleOAuthProvider clientId="AIzaSyAZGwBBvMhT8oi_tR2VV3rckuVeNWzzE2k">
                    <GoogleLogin 
                        onSuccess={credentialResponse => {
                            console.log(credentialResponse);
                        }}
                        onError={() => {
                            console.log('Login Failed');
                        }}
                    />    
                </GoogleOAuthProvider>
            </View>
        </View>
    );
};

export default Login;
