import * as React from 'react';
import { ScrollView, View } from 'react-native';
import { List, Text, useTheme } from 'react-native-paper';
import { getAllUsers } from '../services/adminService';

const UsersSection = () => {
    const [users, setUsers] = React.useState([]);
    const [expandedIds, setExpandedIds] = React.useState(new Set());
    const { theme } = React.useContext(PreferencesContext);

    const handlePress = (id) => {
        const newSet = new Set(expandedIds);
        if (newSet.has(id)) {
            newSet.delete(id);
        } else {
            newSet.add(id);
        }
        setExpandedIds(newSet);
    };

    React.useEffect(() => {
        getAllUsers().then(setUsers).catch(console.error);
    }, []);

    return (
        <ScrollView style={{ padding: 20 }}>
            {users.map((user) => (
                <View
                    key={user.id}
                    style={{
                        backgroundColor: theme.colors.listItem,
                        margin: 15,
                        marginTop: 15,
                        marginBottom: 0,
                        borderRadius: 10,
                        shadowColor: "#000",
                        shadowOffset: { width: 0, height: 2 },
                        shadowOpacity: 0.23,
                        shadowRadius: 2.62,
                        elevation: 4,
                    }}
                >
                    <List.Accordion
                        title={user.username}
                        expanded={expandedIds.has(user.id)}
                        onPress={() => handlePress(user.id)}
                        style={{
                            backgroundColor: theme.colors.listItem,
                            borderRadius: 10,
                            borderBottomEndRadius: expandedIds.has(user.id) ? 0 : 10,
                            borderBottomStartRadius: expandedIds.has(user.id) ? 0 : 10,
                        }}
                    >
                        <Text style={{ padding: 10, color: theme.colors.primary, fontSize: 18 }}>
                            Email: {user.email}
                        </Text>
                        <Text style={{ padding: 10, color: theme.colors.primary, fontSize: 18 }}>
                            Role: {user.role.name}
                        </Text>
                    </List.Accordion>
                </View>
            ))}
        </ScrollView>
    );
}

export default UsersSection;
