import * as React from 'react';
import { ScrollView, View } from 'react-native';
import { TextInput, Button, useTheme } from 'react-native-paper';
import { PreferencesContext } from '../index';

const AddUserSection = () => {
    const { theme } = React.useContext(PreferencesContext);
    
    const [nombre, setNombre] = React.useState('');
    const [apellido, setApellido] = React.useState('');
    const [edad, setEdad] = React.useState('');
    const [telefono, setTelefono] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [direccion, setDireccion] = React.useState('');

    const handleSubmit = () => {
        // Implementar lógica de envío de datos
        console.log("Enviando datos del usuario:", { nombre, apellido, edad, telefono, email, direccion });
        // Aquí podrías llamar a un API para guardar los datos o hacer alguna otra acción
    };

    return (
        <ScrollView style={{ padding: 20 }}>
            <TextInput
                label="Nombre"
                value={nombre}
                onChangeText={setNombre}
                mode="outlined"
                style={{ marginBottom: 10 }}
            />
            <TextInput
                label="Apellido"
                value={apellido}
                onChangeText={setApellido}
                mode="outlined"
                style={{ marginBottom: 10 }}
            />
            <TextInput
                label="Edad"
                value={edad}
                onChangeText={setEdad}
                mode="outlined"
                style={{ marginBottom: 10 }}
                keyboardType="numeric"
            />
            <TextInput
                label="Teléfono"
                value={telefono}
                onChangeText={setTelefono}
                mode="outlined"
                style={{ marginBottom: 10 }}
                keyboardType="phone-pad"
            />
            <TextInput
                label="Correo Electrónico"
                value={email}
                onChangeText={setEmail}
                mode="outlined"
                style={{ marginBottom: 10 }}
                keyboardType="email-address"
            />
            <TextInput
                label="Dirección"
                value={direccion}
                onChangeText={setDireccion}
                mode="outlined"
                style={{ marginBottom: 10 }}
            />
            <Button 
                mode="contained" 
                onPress={handleSubmit}
                style={{ marginTop: 10, height: 50, width: '50%', alignSelf: 'center'}}
                contentStyle={{
                    justifyContent: 'center',
                    alignItems: 'center',
                    alignContent: 'center',
                }}>
                Agregar Usuario
            </Button>
        </ScrollView>
    );
}

export default AddUserSection;
