import { Background } from '@react-navigation/elements';
import * as React from 'react';
import { ScrollView } from 'react-native';
import { List, useTheme } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../RootStackParamList';
import { PreferencesContext } from '../index';

import itemsData from '../data/itemsData.json';

const QualificacionsSection = () => {
    
    const { theme } = React.useContext(PreferencesContext);
    
    type NewsNavigationProp = StackNavigationProp<RootStackParamList, 'InfoSection'>

    const navigation = useNavigation<NewsNavigationProp>();

    return (
        <ScrollView>
            <List.Accordion
                title="Título del Acordeón" // Puedes personalizar este título
                left={props => <List.Icon {...props} icon="folder" />} // Icono del acordeón, cámbialo según necesites
                style={{
                    backgroundColor: theme.colors.listItem,
                    margin: 15,
                    marginBottom: 0,
                    borderRadius: 10,
                    shadowColor: "#000",
                    shadowOffset: {
                        width: 0,
                        height: 2,
                    },
                    shadowOpacity: 0.23,
                    shadowRadius: 2.62,
                    elevation: 4, // Estilo de sombreado para Android
                }}
            >
                {itemsData.map((item) => (
                    <List.Item
                        key={item.id}
                        title={item.title}
                        left={(props) => <List.Icon {...props} icon={item.icon} />}
                        onPress={() => navigation.navigate('InfoItem', {
                            titulo: item.title,
                            subtitulo: item.subtitle,
                            texto: item.text,
                        })}
                    />
                ))}
            </List.Accordion>
        </ScrollView>
    );
}

export default QualificacionsSection;
