import { Background } from '@react-navigation/elements';
import * as React from 'react';
import { ScrollView } from 'react-native';
import { List, useTheme } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../RootStackParamList';
import { PreferencesContext } from '../index';

import qualificacionsData from '../data/qualificacionsData.json';

const QualificacionsSection = () => {
    
    const { theme } = React.useContext(PreferencesContext);

    return (
        <ScrollView>
            {qualificacionsData.map((item) => (
                <List.Accordion
                    key={item.id}
                    title={item.title}
                    left={(props) => <List.Icon {...props} icon={item.icon} />}
                    style={{
                        backgroundColor: theme.colors.listItem,
                        margin: 15,
                        marginTop: 15,
                        borderRadius: 10,
                        shadowColor: "#000",
                        shadowOffset: {
                            width: 0,
                            height: 2,
                        },
                        shadowOpacity: 0.23,
                        shadowRadius: 2.62,
                        elevation: 4, // Estilo de sombreado para Android
                    }}>
                    {/* Aquí asumimos que cada item puede tener un array de subItems */}
                    {item.subItems?.map((subItem, index) => (
                        <List.Item
                            key={index}
                            title={`Nombre: ${subItem.name}`}
                            description={`Detalle: ${subItem.detail}`}
                            titleNumberOfLines={5}
                            descriptionNumberOfLines={5}
                            left={props => <List.Icon {...props} icon="star-outline" />}
                        />
                    ))}
                    {/* Si no hay subItems, se muestra el item principal */}
                    {!item.subItems && (
                        <List.Item
                            title={`Subtítulo: ${item.subtitle}`}
                            description={`Texto: ${item.text}`}
                            titleNumberOfLines={5}
                            descriptionNumberOfLines={5}
                        />
                    )}
                </List.Accordion>
            ))}
        </ScrollView>
    );
}

export default QualificacionsSection;
