import * as React from 'react';
import { ScrollView } from 'react-native';
import { Avatar, Button, Card, Text } from 'react-native-paper';

// Estructura de datos para las noticias
const newsData = [
  {
    id: 1,
    title: "Noticia 1",
    subtitle: "Subtítulo de la noticia 1",
    content: "Contenido de la noticia 1",
    imageUrl: "https://picsum.photos/700?image=1",
  },
  {
    id: 2,
    title: "Noticia 2",
    subtitle: "Subtítulo de la noticia 2",
    content: "Contenido de la noticia 2",
    imageUrl: "https://picsum.photos/700?image=2",
  },
  // Más noticias...
];

const LeftContent = props => <Avatar.Icon {...props} icon="newspaper" />

const NewsSection = () => (
  <ScrollView>
    {newsData.map((newsItem) => (
      <Card key={newsItem.id} style={{ margin: 10 }}>
        <Card.Cover source={{ uri: newsItem.imageUrl }} />
        <Card.Content style={{ marginTop: 10 }}>
          <Text variant="titleLarge" style={{ marginBottom: 20 }}>{newsItem.title}</Text>
          <Text variant="bodyMedium">{newsItem.content}</Text>
        </Card.Content>
      </Card>
    ))}
  </ScrollView>
);

export default NewsSection;
