import { Background } from '@react-navigation/elements';
import * as React from 'react';
import { ScrollView, View, Text } from 'react-native';
import { List, useTheme } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../RootStackParamList';
import { PreferencesContext } from '../index';

import qualificacionsData from '../data/qualificacionsData.json';

const QualificacionsSection = () => {
    
    const { theme } = React.useContext(PreferencesContext);

    return (
        <ScrollView>
            {qualificacionsData.map((item, itemIndex, array) => (
                <View
                    key={item.id}
                    style={{backgroundColor: theme.colors.listItem,
                        margin: 15,
                        marginTop: 15,
                        borderRadius: 10,
                        shadowColor: "#000",
                        shadowOffset: {
                            width: 0,
                            height: 2,
                        },
                        shadowOpacity: 0.23,
                        shadowRadius: 2.62,
                        elevation: 4,
                    }}
                >
                    <List.Accordion
                        title={item.title}
                        description={item.professor}
                        right={() => (
                            <View style={{backgroundColor: '#E0E0E0',
                                paddingHorizontal: 8,
                                paddingVertical: 4,
                                borderRadius: 4,
                                marginLeft: 8,}}>
                                <Text>{item.nota}</Text>
                            </View>
                        )}
                        style={{
                            backgroundColor: theme.colors.listItem,
                        }}
                    >
                        {item.ufs.map((uf, index) => (
                            <List.Item
                                key={index}
                                title={uf.name}
                                description={uf.detail}
                                left={props => <List.Icon {...props} icon="book-outline" />}
                                right={() => (
                                    <View style={{backgroundColor: '#E0E0E0',
                                        margin: 10}}>
                                        <Text>{uf.nota}</Text>
                                    </View>
                                )}
                                style={{
                                    // Evita aplicar sombras y bordes a los Items individuales.
                                }}
                            />
                        ))}
                    </List.Accordion>
                </View>
            ))}
        </ScrollView>
    );
}

export default QualificacionsSection;
